pragma solidity ^0.8.0;

import "../../../../../openzeppelin/token/ERC20/ERC20.sol";
import "./IComposableOFT.sol";
import "./ComposableOFTCore.sol";

contract ComposableOFT is ComposableOFTCore, ERC20, IComposableOFT {
    constructor(
        string memory _name,
        string memory _symbol,
        address _lzEndpoint
    ) ERC20(_name, _symbol) ComposableOFTCore(_lzEndpoint) {}

    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(ComposableOFTCore, IERC165) returns (bool) {
        return
            interfaceId == type(IComposableOFT).interfaceId ||
            interfaceId == type(IOFT).interfaceId ||
            interfaceId == type(IERC20).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function circulatingSupply() public view virtual override returns (uint) {
        return totalSupply();
    }

    function token() public view virtual override returns (address) {
        return address(this);
    }

    function _debitFrom(address _from, uint16, bytes memory, uint _amount) internal virtual override returns (uint) {
        address spender = _msgSender();
        if (_from != spender) _spendAllowance(_from, spender, _amount);
        _burn(_from, _amount);
        return _amount;
    }

    function _creditTo(uint16, address _toAddress, uint _amount) internal virtual override returns (uint) {
        _mint(_toAddress, _amount);
        return _amount;
    }
}

